import React, { useState, useRef, useEffect } from 'react';
import styled from '@emotion/styled';
import { useForm } from 'react-hook-form';
import axios from 'axios';
import SimpleDatePicker from '../components/common/SimpleDatePicker';

const PageContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 2rem;
`;

const Card = styled.div`
  background: white;
  border-radius: 10px;
  padding: 2rem;
  margin-bottom: 2rem;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const Title = styled.h1`
  color: #1f2937;
  margin-bottom: 1.5rem;
  font-size: 2rem;
`;

const FormGroup = styled.div`
  margin-bottom: 1.5rem;
`;

const Label = styled.label`
  display: block;
  margin-bottom: 0.5rem;
  color: #4b5563;
  font-weight: 500;
`;

const Select = styled.select`
  width: 100%;
  padding: 0.75rem;
  border: 2px solid #e2e8f0;
  border-radius: 8px;
  font-size: 1rem;

  &:focus {
    border-color: #3b82f6;
    outline: none;
    box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
  }
`;

const Button = styled.button<{ primary?: boolean; loading?: boolean }>`
  padding: 0.75rem 1.5rem;
  border: none;
  border-radius: 8px;
  font-weight: 600;
  cursor: ${props => props.loading ? 'wait' : 'pointer'};
  opacity: ${props => props.loading ? 0.7 : 1};
  transition: all 0.3s ease;
  margin-right: 1rem;

  ${props => props.primary ? `
    background: #3b82f6;
    color: white;
    &:hover { background: #2563eb; }
  ` : `
    background: #e2e8f0;
    color: #1f2937;
    &:hover { background: #cbd5e1; }
  `}
`;

const ErrorMessage = styled.p`
  color: #ef4444;
  font-size: 0.875rem;
  margin-top: 0.5rem;
`;

const SuccessMessage = styled.div`
  background-color: #10b981;
  color: white;
  padding: 1rem;
  border-radius: 8px;
  margin-bottom: 1rem;
`;

const LoadingSpinner = styled.div`
  display: inline-block;
  width: 20px;
  height: 20px;
  border: 3px solid #f3f3f3;
  border-top: 3px solid #3498db;
  border-radius: 50%;
  animation: spin 1s linear infinite;
  margin-right: 8px;

  @keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }
`;

// ÌååÏùº ÏóÖÎ°úÎìú Í¥ÄÎ†® Ïä§ÌÉÄÏùº
const FileUploadContainer = styled.div`
  border: 2px dashed #e2e8f0;
  border-radius: 8px;
  padding: 2rem;
  text-align: center;
  margin-bottom: 1.5rem;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    border-color: #3b82f6;
    background-color: #f8fafc;
  }
`;

const FileInput = styled.input`
  display: none;
`;

const FilePreview = styled.div`
  margin-top: 1rem;
  padding: 1rem;
  background-color: #f8fafc;
  border-radius: 8px;
  display: flex;
  align-items: center;
  justify-content: space-between;
`;

const FileIcon = styled.div`
  margin-right: 1rem;
  color: #3b82f6;
  font-size: 1.5rem;
`;

const FileInfo = styled.div`
  flex: 1;
  text-align: left;
`;

const FileName = styled.div`
  font-weight: 500;
  margin-bottom: 0.25rem;
`;

const FileSize = styled.div`
  font-size: 0.875rem;
  color: #6b7280;
`;

const RemoveFileButton = styled.button`
  background: none;
  border: none;
  color: #ef4444;
  cursor: pointer;
  font-size: 1.25rem;
`;

// ÎßàÌÅ¨Îã§Ïö¥ ÎØ∏Î¶¨Î≥¥Í∏∞ Ïª®ÌÖåÏù¥ÎÑà
const MarkdownPreview = styled.div`
  margin-top: 1rem;
  padding: 1rem;
  background-color: #f8fafc;
  border-radius: 8px;
  border: 1px solid #e2e8f0;
  max-height: 300px;
  overflow-y: auto;
`;

interface FormData {
  bookTitle: string;
}

// Ï±Ö Î™©Î°ù Îç∞Ïù¥ÌÑ∞
const bookOptions = [
  { value: "ÌçºÏä§ÎÑê MBA", label: "ÌçºÏä§ÎÑê MBA" },
  { value: "ÏïÑÏ£º ÏûëÏùÄ ÏäµÍ¥ÄÏùò Ìûò", label: "ÏïÑÏ£º ÏûëÏùÄ ÏäµÍ¥ÄÏùò Ìûò" },
  { value: "ÏÇ¨ÌîºÏóîÏä§", label: "ÏÇ¨ÌîºÏóîÏä§" },
  { value: "ÏÉùÍ∞ÅÏóê Í¥ÄÌïú ÏÉùÍ∞Å", label: "ÏÉùÍ∞ÅÏóê Í¥ÄÌïú ÏÉùÍ∞Å" },
  { value: "Îç∞Ïùº Ïπ¥ÎÑ§Í∏∞Ïùò Ïù∏Í∞ÑÍ¥ÄÍ≥ÑÎ°†", label: "Îç∞Ïùº Ïπ¥ÎÑ§Í∏∞Ïùò Ïù∏Í∞ÑÍ¥ÄÍ≥ÑÎ°†" }
];

// Í∞ÑÎã®Ìïú ÎßàÌÅ¨Îã§Ïö¥ ÎØ∏Î¶¨Î≥¥Í∏∞ Ïª¥Ìè¨ÎÑåÌä∏
const SimpleMarkdownPreview: React.FC<{ content: string }> = ({ content }) => {
  return (
    <div style={{ whiteSpace: 'pre-wrap' }}>
      {content}
    </div>
  );
};

const DiscussionRegister: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);

  // ÌååÏùº ÏóÖÎ°úÎìú Í¥ÄÎ†® ÏÉÅÌÉú
  const [summaryFile, setSummaryFile] = useState<File | null>(null);
  const [applicationFile, setApplicationFile] = useState<File | null>(null);
  const summaryFileInputRef = useRef<HTMLInputElement>(null);
  const applicationFileInputRef = useRef<HTMLInputElement>(null);

  // ÌååÏùº ÎÇ¥Ïö© ÏÉÅÌÉú Ï∂îÍ∞Ä
  const [summaryContent, setSummaryContent] = useState<string>('');
  const [applicationContent, setApplicationContent] = useState<string>('');

  const { register, handleSubmit, formState: { errors }, reset } = useForm<FormData>({
    defaultValues: {
      bookTitle: "ÌçºÏä§ÎÑê MBA" // Í∏∞Î≥∏Í∞í ÏÑ§Ï†ï
    }
  });

  // ÌååÏùº ÌÅ¨Í∏∞ Ìè¨Îß∑ÌåÖ Ìï®Ïàò
  const formatFileSize = (bytes: number): string => {
    if (bytes < 1024) return bytes + ' bytes';
    else if (bytes < 1048576) return (bytes / 1024).toFixed(1) + ' KB';
    else return (bytes / 1048576).toFixed(1) + ' MB';
  };

  // ÌååÏùº ÏóÖÎ°úÎìú Í¥ÄÎ†® Ìï®Ïàò
  const handleSummaryFileClick = () => {
    if (summaryFileInputRef.current) {
      summaryFileInputRef.current.click();
    }
  };

  const handleApplicationFileClick = () => {
    if (applicationFileInputRef.current) {
      applicationFileInputRef.current.click();
    }
  };

  // ÌååÏùº ÎÇ¥Ïö© ÏùΩÍ∏∞ Ìï®Ïàò
  const readFileContent = (file: File): Promise<string> => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = (event) => {
        if (event.target?.result) {
          resolve(event.target.result as string);
        } else {
          reject(new Error('ÌååÏùºÏùÑ ÏùΩÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.'));
        }
      };
      reader.onerror = (error) => reject(error);
      reader.readAsText(file);
    });
  };

  // ÏöîÏïΩ ÌååÏùº Î≥ÄÍ≤Ω Ìï∏Îì§Îü¨ ÏàòÏ†ï
  const handleSummaryFileChange = async (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files.length > 0) {
      const file = e.target.files[0];
      if (file.type === 'text/markdown' || file.name.endsWith('.md')) {
        setSummaryFile(file);
        try {
          const content = await readFileContent(file);
          setSummaryContent(content);
        } catch (error) {
          console.error('ÌååÏùº ÏùΩÍ∏∞ Ïò§Î•ò:', error);
          alert('ÌååÏùºÏùÑ ÏùΩÎäî Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
        }
      } else {
        alert('ÎßàÌÅ¨Îã§Ïö¥(.md) ÌååÏùºÎßå ÏóÖÎ°úÎìú Í∞ÄÎä•Ìï©ÎãàÎã§.');
      }
    }
  };

  // Ï†ÅÏö© ÌååÏùº Î≥ÄÍ≤Ω Ìï∏Îì§Îü¨ ÏàòÏ†ï
  const handleApplicationFileChange = async (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files.length > 0) {
      const file = e.target.files[0];
      if (file.type === 'text/markdown' || file.name.endsWith('.md')) {
        setApplicationFile(file);
        try {
          const content = await readFileContent(file);
          setApplicationContent(content);
        } catch (error) {
          console.error('ÌååÏùº ÏùΩÍ∏∞ Ïò§Î•ò:', error);
          alert('ÌååÏùºÏùÑ ÏùΩÎäî Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
        }
      } else {
        alert('ÎßàÌÅ¨Îã§Ïö¥(.md) ÌååÏùºÎßå ÏóÖÎ°úÎìú Í∞ÄÎä•Ìï©ÎãàÎã§.');
      }
    }
  };

  // ÌååÏùº Ï†úÍ±∞ Ìï∏Îì§Îü¨ ÏàòÏ†ï
  const removeSummaryFile = () => {
    setSummaryFile(null);
    setSummaryContent('');
    if (summaryFileInputRef.current) {
      summaryFileInputRef.current.value = '';
    }
  };

  const removeApplicationFile = () => {
    setApplicationFile(null);
    setApplicationContent('');
    if (applicationFileInputRef.current) {
      applicationFileInputRef.current.value = '';
    }
  };

  // Ìèº Ï†úÏ∂ú Ìï∏Îì§Îü¨ ÏàòÏ†ï
  const onSubmit = async (data: FormData) => {
    if (!summaryFile || !summaryContent) {
      alert('ÎèÖÏÑúÌÜ†Î°† ÏöîÏïΩ ÌååÏùº(md)ÏùÑ ÏóÖÎ°úÎìúÌï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    if (!applicationFile || !applicationContent) {
      alert('ÎèÖÏÑúÌÜ†Î°† Ï†ÅÏö© ÌååÏùº(md)ÏùÑ ÏóÖÎ°úÎìúÌï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    setLoading(true);
    try {
      // ÏöîÏ≤≠ Îç∞Ïù¥ÌÑ∞ Íµ¨ÏÑ±
      const requestData = {
        bookTitle: data.bookTitle,
        summaryContent: summaryContent,
        applicationContent: applicationContent,
        summaryFileName: summaryFile.name,
        applicationFileName: applicationFile.name
      };

      console.log('ÏöîÏ≤≠ Îç∞Ïù¥ÌÑ∞:', {
        bookTitle: data.bookTitle,
        summaryFileName: summaryFile.name,
        applicationFileName: applicationFile.name,
        summaryContentLength: summaryContent.length,
        applicationContentLength: applicationContent.length
      });

      const apiUrl = `${process.env.REACT_APP_API_URL || 'http://localhost:5001/api'}/discussions`;
      console.log('API URL:', apiUrl);

      const response = await axios.post(apiUrl, requestData);

      console.log('ÏùëÎãµ Îç∞Ïù¥ÌÑ∞:', response.data);

      setSuccess(true);

      // Ìèº Ï¥àÍ∏∞Ìôî
      reset();
      setSummaryFile(null);
      setApplicationFile(null);
      setSummaryContent('');
      setApplicationContent('');

      // 3Ï¥à ÌõÑ ÏÑ±Í≥µ Î©îÏãúÏßÄ Ïà®Í∏∞Í∏∞
      setTimeout(() => {
        setSuccess(false);
      }, 3000);
    } catch (error) {
      console.error('ÎèÖÏÑú ÌÜ†Î°† Îì±Î°ù Ïò§Î•ò:', error);

      // ÏÉÅÏÑ∏ Ïò§Î•ò Ï†ïÎ≥¥ ÌëúÏãú
      if (axios.isAxiosError(error)) {
        if (error.response) {
          console.error('ÏùëÎãµ Ïò§Î•ò Îç∞Ïù¥ÌÑ∞:', error.response.data);
          console.error('ÏùëÎãµ ÏÉÅÌÉú:', error.response.status);
          console.error('ÏùëÎãµ Ìó§Îçî:', error.response.headers);

          alert(`Ïò§Î•ò (${error.response.status}): ${error.response.data.error || 'ÏÑúÎ≤Ñ Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.'}`);
        } else if (error.request) {
          console.error('ÏöîÏ≤≠ÏùÄ Ï†ÑÏÜ°ÎêòÏóàÏßÄÎßå ÏùëÎãµÏù¥ ÏóÜÏäµÎãàÎã§:', error.request);
          alert('ÏÑúÎ≤ÑÎ°úÎ∂ÄÌÑ∞ ÏùëÎãµÏù¥ ÏóÜÏäµÎãàÎã§. ÎÑ§Ìä∏ÏõåÌÅ¨ Ïó∞Í≤∞ÏùÑ ÌôïÏù∏Ìï¥Ï£ºÏÑ∏Ïöî.');
        } else {
          console.error('ÏöîÏ≤≠ ÏÑ§Ï†ï Ï§ë Ïò§Î•ò Î∞úÏÉù:', error.message);
          alert(`ÏöîÏ≤≠ Ïò§Î•ò: ${error.message}`);
        }
      } else {
        alert(`ÎèÖÏÑú ÌÜ†Î°† Îì±Î°ù Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§: ${(error as Error).message}`);
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <PageContainer>
      <Title>üìö ÎèÖÏÑú ÌÜ†Î°† Îì±Î°ù</Title>

      {success && (
        <SuccessMessage>
          ÎèÖÏÑú ÌÜ†Î°†Ïù¥ ÏÑ±Í≥µÏ†ÅÏúºÎ°ú Îì±Î°ùÎêòÏóàÏäµÎãàÎã§!
        </SuccessMessage>
      )}

      <Card>
        <form onSubmit={handleSubmit(onSubmit)}>
          <FormGroup>
            <Label htmlFor="bookTitle">Ï±Ö Ï†úÎ™©</Label>
            <Select
              id="bookTitle"
              {...register('bookTitle', { required: 'Ï±Ö Ï†úÎ™©ÏùÑ ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.' })}
            >
              {bookOptions.map(option => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </Select>
            {errors.bookTitle && <ErrorMessage>{errors.bookTitle.message}</ErrorMessage>}
          </FormGroup>

          <FormGroup>
            <Label>ÎèÖÏÑúÌÜ†Î°† ÏöîÏïΩ ÌååÏùº (md)</Label>
            <FileUploadContainer onClick={handleSummaryFileClick}>
              <FileInput 
                type="file" 
                ref={summaryFileInputRef} 
                onChange={handleSummaryFileChange}
                accept=".md"
              />
              {!summaryFile ? (
                <>
                  <div>Drag and drop file here</div>
                  <div style={{ fontSize: '0.875rem', color: '#6b7280', marginTop: '0.5rem' }}>
                    Limit 200MB per file ‚Ä¢ MD
                  </div>
                </>
              ) : (
                <FilePreview>
                  <FileIcon>üìÑ</FileIcon>
                  <FileInfo>
                    <FileName>{summaryFile.name}</FileName>
                    <FileSize>{formatFileSize(summaryFile.size)}</FileSize>
                  </FileInfo>
                  <RemoveFileButton type="button" onClick={(e) => {
                    e.stopPropagation();
                    removeSummaryFile();
                  }}>
                    √ó
                  </RemoveFileButton>
                </FilePreview>
              )}
            </FileUploadContainer>
            {summaryContent && (
              <MarkdownPreview>
                <h4>ÌååÏùº ÎÇ¥Ïö© ÎØ∏Î¶¨Î≥¥Í∏∞:</h4>
                <SimpleMarkdownPreview content={summaryContent} />
              </MarkdownPreview>
            )}
          </FormGroup>

          <FormGroup>
            <Label>ÎèÖÏÑúÌÜ†Î°† Ï†ÅÏö© ÌååÏùº (md)</Label>
            <FileUploadContainer onClick={handleApplicationFileClick}>
              <FileInput 
                type="file" 
                ref={applicationFileInputRef} 
                onChange={handleApplicationFileChange}
                accept=".md"
                style={{ display: 'none' }}
              />
              {applicationFile ? (
                <FilePreview>
                  <FileIcon>üìÑ</FileIcon>
                  <FileInfo>
                    <FileName>{applicationFile?.name}</FileName>
                    <FileSize>{formatFileSize(applicationFile?.size)}</FileSize>
                  </FileInfo>
                  <RemoveFileButton 
                    type="button" 
                    onClick={(e) => {
                      e.stopPropagation();
                      removeApplicationFile();
                    }}
                  >
                    ‚úï
                  </RemoveFileButton>
                </FilePreview>
              ) : (
                <UploadPrompt>
                  ÌÅ¥Î¶≠ÌïòÏó¨ ÌååÏùºÏùÑ ÏóÖÎ°úÎìúÌïòÏÑ∏Ïöî
                </UploadPrompt>
              )}
            </FileUploadContainer>
            {applicationContent && (
              <MarkdownPreview>
                <h4>ÌååÏùº ÎÇ¥Ïö© ÎØ∏Î¶¨Î≥¥Í∏∞:</h4>
                <SimpleMarkdownPreview content={applicationContent} />
              </MarkdownPreview>
            )}
          </FormGroup>

          <Button primary type="submit" disabled={loading}>
            {loading && <LoadingSpinner />}
            {loading ? 'Îì±Î°ù Ï§ë...' : 'ÎèÖÏÑú ÌÜ†Î°† Îì±Î°ù'}
          </Button>
        </form>
      </Card>
    </PageContainer>
  );
};

export default DiscussionRegister;
