interface Discussion {
  id: number;
  bookTitle: string;
  author: string;
  discussionDate: string;
  participants: string[];
  mainTopics: string[];
  summary: string;
  createdAt: string;
}

const DiscussionSearch: React.FC = () => {
  const [bookTitle, setBookTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [startDate, setStartDate] = useState<Date | null>(null);
  const [endDate, setEndDate] = useState<Date | null>(null);
  const [discussions, setDiscussions] = useState<Discussion[]>([]);
  const [loading, setLoading] = useState(false);
  const [selectedDiscussion, setSelectedDiscussion] = useState<Discussion | null>(null);
  const [showModal, setShowModal] = useState(false);

  const searchDiscussions = async () => {
    setLoading(true);
    try {
      const params: any = {};

      if (bookTitle) params.bookTitle = bookTitle;
      if (author) params.author = author;
      if (startDate) params.startDate = startDate.toISOString().split('T')[0];
      if (endDate) params.endDate = endDate.toISOString().split('T')[0];

      const response = await axios.get(`${process.env.REACT_APP_API_URL}/discussions`, { params });
      setDiscussions(response.data);
    } catch (error) {
      console.error('독서 토론 검색 오류:', error);
      alert('독서 토론 검색 중 오류가 발생했습니다.');
    } finally {
      setLoading(false);
    }
  };

  const resetSearch = () => {
    setBookTitle('');
    setAuthor('');
    setStartDate(null);
    setEndDate(null);
    setDiscussions([]);
  };

  const viewDetails = (discussion: Discussion) => {
    setSelectedDiscussion(discussion);
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setSelectedDiscussion(null);
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('ko-KR', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  useEffect(() => {
    // 컴포넌트 마운트 시 모든 토론 가져오기
    searchDiscussions();
  }, []);

  return (
    <PageContainer>
      <Title>🔍 독서 토론 검색</Title>

      <Card>
        <SearchForm>
          <FormGroup>
            <Label htmlFor="bookTitle">책 제목</Label>
            <Input
              id="bookTitle"
              value={bookTitle}
              onChange={(e) => setBookTitle(e.target.value)}
              placeholder="책 제목으로 검색"
            />
          </FormGroup>

          <FormGroup>
            <Label htmlFor="author">저자</Label>
            <Input
              id="author"
              value={author}
              onChange={(e) => setAuthor(e.target.value)}
              placeholder="저자로 검색"
            />
          </FormGroup>

          <FormGroup>
            <Label>토론 날짜 범위</Label>
            <DateRangeContainer>
              <DatePicker
                selected={startDate}
                onChange={(date: Date) => setStartDate(date)}
                selectsStart
                startDate={startDate}
                endDate={endDate}
                dateFormat="yyyy-MM-dd"
                placeholderText="시작일"
                customInput={<Input />}
              />
              <span>~</span>
              <DatePicker
                selected={endDate}
                onChange={(date: Date) => setEndDate(date)}
                selectsEnd
                startDate={startDate}
                endDate={endDate}
                minDate={startDate}
                dateFormat="yyyy-MM-dd"
                placeholderText="종료일"
                customInput={<Input />}
              />
            </DateRangeContainer>
          </FormGroup>
        </SearchForm>

        <div style={{ marginTop: '1rem' }}>
          <Button primary onClick={searchDiscussions} disabled={loading}>
            {loading && <LoadingSpinner />}
            {loading ? '검색 중...' : '검색'}
          </Button>
          <Button onClick={resetSearch}>초기화</Button>
        </div>
      </Card>

      <Card>
        <h2>검색 결과</h2>

        {discussions.length > 0 ? (
          <Table>
            <thead>
              <tr>
                <Th>책 제목</Th>
                <Th>저자</Th>
                <Th>토론 날짜</Th>
                <Th>참여자</Th>
                <Th>상세 정보</Th>
              </tr>
            </thead>
            <tbody>
              {discussions.map((discussion) => (
                <Tr key={discussion.id}>
                  <Td>{discussion.bookTitle}</Td>
                  <Td>{discussion.author}</Td>
                  <Td>{formatDate(discussion.discussionDate)}</Td>
                  <Td>{discussion.participants.join(', ')}</Td>
                  <Td>
                    <DetailsButton onClick={() => viewDetails(discussion)}>
                      상세 보기
                    </DetailsButton>
                  </Td>
                </Tr>
              ))}
            </tbody>
          </Table>
        ) : (
          <NoResults>
            검색 결과가 없습니다.
          </NoResults>
        )}
      </Card>

      {showModal && selectedDiscussion && (
        <Modal onClick={(e) => e.target === e.currentTarget && closeModal()}>
          <ModalContent onClick={(e) => e.stopPropagation()}>
            <ModalHeader>
              <ModalTitle>{selectedDiscussion.bookTitle}</ModalTitle>
              <CloseButton onClick={closeModal}>×</CloseButton>
            </ModalHeader>

            <DetailItem>
              <DetailLabel>저자</DetailLabel>
              <DetailValue>{selectedDiscussion.author}</DetailValue>
            </DetailItem>

            <DetailItem>
              <DetailLabel>토론 날짜</DetailLabel>
              <DetailValue>{formatDate(selectedDiscussion.discussionDate)}</DetailValue>
            </DetailItem>

            <DetailItem>
              <DetailLabel>참여자</DetailLabel>
              <DetailValue>
                {selectedDiscussion.participants.map((participant, index) => (
                  <Tag key={index}>{participant}</Tag>
                ))}
              </DetailValue>
            </DetailItem>

            <DetailItem>
              <DetailLabel>주요 토론 주제</DetailLabel>
              <DetailValue>
                {selectedDiscussion.mainTopics.map((topic, index) => (
                  <Tag key={index}>{topic}</Tag>
                ))}
              </DetailValue>
            </DetailItem>

            <DetailItem>
              <DetailLabel>요약</DetailLabel>
              <DetailValue style={{ whiteSpace: 'pre-line' }}>
                {selectedDiscussion.summary}
              </DetailValue>
            </DetailItem>
          </ModalContent>
        </Modal>
      )}
    </PageContainer>
  );
};

export default DiscussionSearch;
