import React, { useState, useRef } from 'react';
import styled from '@emotion/styled';
import { useForm, Controller } from 'react-hook-form';
import axios from 'axios';
import { OpenAI } from '../services/openai';
import SimpleDatePicker from '../components/common/SimpleDatePicker';

const PageContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 2rem;
`;

const Card = styled.div`
  background: white;
  border-radius: 10px;
  padding: 2rem;
  margin-bottom: 2rem;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const Title = styled.h1`
  color: #1f2937;
  margin-bottom: 1.5rem;
  font-size: 2rem;
`;

const FormGroup = styled.div`
  margin-bottom: 1.5rem;
`;

const Label = styled.label`
  display: block;
  margin-bottom: 0.5rem;
  color: #4b5563;
  font-weight: 500;
`;

const Input = styled.input`
  width: 100%;
  padding: 0.75rem;
  border: 2px solid #e2e8f0;
  border-radius: 8px;
  font-size: 1rem;

  &:focus {
    border-color: #3b82f6;
    outline: none;
    box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
  }
`;

const TextArea = styled.textarea`
  width: 100%;
  padding: 0.75rem;
  border: 2px solid #e2e8f0;
  border-radius: 8px;
  font-size: 1rem;
  min-height: 150px;
  resize: vertical;

  &:focus {
    border-color: #3b82f6;
    outline: none;
    box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
  }
`;

const Button = styled.button<{ primary?: boolean; loading?: boolean }>`
  padding: 0.75rem 1.5rem;
  border: none;
  border-radius: 8px;
  font-weight: 600;
  cursor: ${props => props.loading ? 'wait' : 'pointer'};
  opacity: ${props => props.loading ? 0.7 : 1};
  transition: all 0.3s ease;
  margin-right: 1rem;

  ${props => props.primary ? `
    background: #3b82f6;
    color: white;
    &:hover { background: #2563eb; }
  ` : `
    background: #e2e8f0;
    color: #1f2937;
    &:hover { background: #cbd5e1; }
  `}
`;

const ErrorMessage = styled.p`
  color: #ef4444;
  font-size: 0.875rem;
  margin-top: 0.5rem;
`;

const SuccessMessage = styled.div`
  background-color: #10b981;
  color: white;
  padding: 1rem;
  border-radius: 8px;
  margin-bottom: 1rem;
`;

const TagInput = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
  margin-top: 0.5rem;
`;

const Tag = styled.div`
  background: #e2e8f0;
  padding: 0.5rem;
  border-radius: 4px;
  display: flex;
  align-items: center;
  gap: 0.5rem;
`;

const RemoveTagButton = styled.button`
  background: none;
  border: none;
  color: #4b5563;
  cursor: pointer;
  font-size: 1rem;
  padding: 0;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const LoadingSpinner = styled.div`
  display: inline-block;
  width: 20px;
  height: 20px;
  border: 3px solid #f3f3f3;
  border-top: 3px solid #3498db;
  border-radius: 50%;
  animation: spin 1s linear infinite;
  margin-right: 8px;

  @keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }
`;

// 파일 업로드 관련 스타일 추가
const FileUploadContainer = styled.div`
  border: 2px dashed #e2e8f0;
  border-radius: 8px;
  padding: 2rem;
  text-align: center;
  margin-bottom: 1.5rem;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    border-color: #3b82f6;
    background-color: #f8fafc;
  }
`;

const FileInput = styled.input`
  display: none;
`;

const FilePreview = styled.div`
  margin-top: 1rem;
  padding: 1rem;
  background-color: #f8fafc;
  border-radius: 8px;
  display: flex;
  align-items: center;
  justify-content: space-between;
`;

const FileIcon = styled.div`
  margin-right: 1rem;
  color: #3b82f6;
  font-size: 1.5rem;
`;

const FileInfo = styled.div`
  flex: 1;
  text-align: left;
`;

const FileName = styled.div`
  font-weight: 500;
  margin-bottom: 0.25rem;
`;

const FileSize = styled.div`
  font-size: 0.875rem;
  color: #6b7280;
`;

const RemoveFileButton = styled.button`
  background: none;
  border: none;
  color: #ef4444;
  cursor: pointer;
  font-size: 1.25rem;
`;

interface FormData {
  bookTitle: string;
  author: string;
  discussionDate: Date;
  participants: string;
  mainTopics: string;
  summary: string;
}

const DiscussionRegister: React.FC = () => {
  const [participants, setParticipants] = useState<string[]>([]);
  const [mainTopics, setMainTopics] = useState<string[]>([]);
  const [currentParticipant, setCurrentParticipant] = useState('');
  const [currentTopic, setCurrentTopic] = useState('');
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);
  const [aiSummaryLoading, setAiSummaryLoading] = useState(false);

  // 파일 업로드 관련 상태 추가
  const [summaryFile, setSummaryFile] = useState<File | null>(null);
  const [applicationFile, setApplicationFile] = useState<File | null>(null);
  const summaryFileInputRef = useRef<HTMLInputElement>(null);
  const applicationFileInputRef = useRef<HTMLInputElement>(null);

  const { register, handleSubmit, control, formState: { errors }, setValue, watch } = useForm<FormData>();

  const addParticipant = () => {
    if (currentParticipant.trim() && !participants.includes(currentParticipant.trim())) {
      setParticipants([...participants, currentParticipant.trim()]);
      setCurrentParticipant('');
    }
  };

  const removeParticipant = (index: number) => {
    setParticipants(participants.filter((_, i) => i !== index));
  };

  const addTopic = () => {
    if (currentTopic.trim() && !mainTopics.includes(currentTopic.trim())) {
      setMainTopics([...mainTopics, currentTopic.trim()]);
      setCurrentTopic('');
    }
  };

  const removeTopic = (index: number) => {
    setMainTopics(mainTopics.filter((_, i) => i !== index));
  };

  const generateAISummary = async () => {
    const bookTitle = watch('bookTitle');
    const author = watch('author');
    const topics = mainTopics.join(', ');

    if (!bookTitle || !author || mainTopics.length === 0) {
      alert('책 제목, 저자, 그리고 최소 하나의 주요 토론 주제를 입력해주세요.');
      return;
    }

    setAiSummaryLoading(true);
    try {
      const prompt = `
        다음 독서 토론에 대한 요약을 작성해주세요:
        책 제목: ${bookTitle}
        저자: ${author}
        주요 토론 주제: ${topics}

        이 책의 주요 내용과 토론 주제에 대한 간결하고 통찰력 있는 요약을 300자 이내로 작성해주세요.
      `;

      const summary = await OpenAI.generateResponse(prompt);
      setValue('summary', summary);
    } catch (error) {
      console.error('AI 요약 생성 오류:', error);
      alert('요약 생성 중 오류가 발생했습니다.');
    } finally {
      setAiSummaryLoading(false);
    }
  };

  // 파일 업로드 관련 함수 추가
  const handleSummaryFileClick = () => {
    if (summaryFileInputRef.current) {
      summaryFileInputRef.current.click();
    }
  };

  const handleApplicationFileClick = () => {
    if (applicationFileInputRef.current) {
      applicationFileInputRef.current.click();
    }
  };

  const handleSummaryFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files.length > 0) {
      const file = e.target.files[0];
      if (file.type === 'text/markdown' || file.name.endsWith('.md')) {
        setSummaryFile(file);
      } else {
        alert('마크다운(.md) 파일만 업로드 가능합니다.');
      }
    }
  };

  const handleApplicationFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files.length > 0) {
      const file = e.target.files[0];
      if (file.type === 'text/markdown' || file.name.endsWith('.md')) {
        setApplicationFile(file);
      } else {
        alert('마크다운(.md) 파일만 업로드 가능합니다.');
      }
    }
  };

  const removeSummaryFile = () => {
    setSummaryFile(null);
    if (summaryFileInputRef.current) {
      summaryFileInputRef.current.value = '';
    }
  };

  const removeApplicationFile = () => {
    setApplicationFile(null);
    if (applicationFileInputRef.current) {
      applicationFileInputRef.current.value = '';
    }
  };

  const formatFileSize = (bytes: number): string => {
    if (bytes < 1024) return bytes + ' bytes';
    else if (bytes < 1048576) return (bytes / 1024).toFixed(1) + ' KB';
    else return (bytes / 1048576).toFixed(1) + ' MB';
  };

  const onSubmit = async (data: FormData) => {
    if (participants.length === 0) {
      alert('최소 한 명의 참여자를 추가해주세요.');
      return;
    }

    if (mainTopics.length === 0) {
      alert('최소 하나의 주요 토론 주제를 추가해주세요.');
      return;
    }

    if (!summaryFile) {
      alert('독서토론 요약 파일(md)을 업로드해주세요.');
      return;
    }

    if (!applicationFile) {
      alert('독서토론 적용 파일(md)을 업로드해주세요.');
      return;
    }

    setLoading(true);
    try {
      const formattedDate = data.discussionDate.toISOString().split('T')[0];

      // FormData 객체 생성
      const formData = new FormData();
      formData.append('bookTitle', data.bookTitle);
      formData.append('author', data.author);
      formData.append('discussionDate', formattedDate);
      formData.append('participants', JSON.stringify(participants));
      formData.append('mainTopics', JSON.stringify(mainTopics));
      formData.append('summary', data.summary);
      formData.append('summaryFile', summaryFile);
      formData.append('applicationFile', applicationFile);

      await axios.post(`${process.env.REACT_APP_API_URL}/discussions`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      setSuccess(true);
      // 폼 초기화
      setValue('bookTitle', '');
      setValue('author', '');
      setValue('discussionDate', new Date());
      setValue('summary', '');
      setParticipants([]);
      setMainTopics([]);
      setSummaryFile(null);
      setApplicationFile(null);

      // 3초 후 성공 메시지 숨기기
      setTimeout(() => {
        setSuccess(false);
      }, 3000);
    } catch (error) {
      console.error('독서 토론 등록 오류:', error);
      alert('독서 토론 등록 중 오류가 발생했습니다.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <PageContainer>
      <Title>📚 독서 토론 등록</Title>

      {success && (
        <SuccessMessage>
          독서 토론이 성공적으로 등록되었습니다!
        </SuccessMessage>
      )}

      <Card>
        <form onSubmit={handleSubmit(onSubmit)}>
          <FormGroup>
            <Label htmlFor="bookTitle">책 제목</Label>
            <Input
              id="bookTitle"
              {...register('bookTitle', { required: '책 제목을 입력해주세요.' })}
            />
            {errors.bookTitle && <ErrorMessage>{errors.bookTitle.message}</ErrorMessage>}
          </FormGroup>

          <FormGroup>
            <Label htmlFor="author">저자</Label>
            <Input
              id="author"
              {...register('author', { required: '저자를 입력해주세요.' })}
            />
            {errors.author && <ErrorMessage>{errors.author.message}</ErrorMessage>}
          </FormGroup>

          <FormGroup>
            <Label htmlFor="discussionDate">토론 날짜</Label>
            <Controller
              control={control}
              name="discussionDate"
              defaultValue={new Date()}
              rules={{ required: '토론 날짜를 선택해주세요.' }}
              render={({ field }) => (
                <SimpleDatePicker
                  selected={field.value}
                  onChange={(date: Date) => field.onChange(date)}
                  placeholderText="날짜 선택"
                />
              )}
            />
            {errors.discussionDate && <ErrorMessage>{errors.discussionDate.message}</ErrorMessage>}
          </FormGroup>

          <FormGroup>
            <Label htmlFor="participants">참여자</Label>
            <div style={{ display: 'flex', gap: '0.5rem' }}>
              <Input
                id="participants"
                value={currentParticipant}
                onChange={(e) => setCurrentParticipant(e.target.value)}
                placeholder="참여자 이름 입력 후 추가 버튼 클릭"
                onKeyPress={(e) => e.key === 'Enter' && (e.preventDefault(), addParticipant())}
              />
              <Button type="button" onClick={addParticipant}>추가</Button>
            </div>
            <TagInput>
              {participants.map((participant, index) => (
                <Tag key={index}>
                  {participant}
                  <RemoveTagButton type="button" onClick={() => removeParticipant(index)}>
                    ×
                  </RemoveTagButton>
                </Tag>
              ))}
            </TagInput>
          </FormGroup>

          <FormGroup>
            <Label htmlFor="mainTopics">주요 토론 주제</Label>
            <div style={{ display: 'flex', gap: '0.5rem' }}>
              <Input
                id="mainTopics"
                value={currentTopic}
                onChange={(e) => setCurrentTopic(e.target.value)}
                placeholder="토론 주제 입력 후 추가 버튼 클릭"
                onKeyPress={(e) => e.key === 'Enter' && (e.preventDefault(), addTopic())}
              />
              <Button type="button" onClick={addTopic}>추가</Button>
            </div>
            <TagInput>
              {mainTopics.map((topic, index) => (
                <Tag key={index}>
                  {topic}
                  <RemoveTagButton type="button" onClick={() => removeTopic(index)}>
                    ×
                  </RemoveTagButton>
                </Tag>
              ))}
            </TagInput>
          </FormGroup>

          <FormGroup>
            <Label>독서토론 요약 파일 (md)</Label>
            <FileUploadContainer onClick={handleSummaryFileClick}>
              <FileInput 
                type="file" 
                ref={summaryFileInputRef} 
                onChange={handleSummaryFileChange}
                accept=".md"
              />
              {!summaryFile ? (
                <>
                  <div>Drag and drop file here</div>
                  <div style={{ fontSize: '0.875rem', color: '#6b7280', marginTop: '0.5rem' }}>
                    Limit 200MB per file • MD
                  </div>
                </>
              ) : (
                <FilePreview>
                  <FileIcon>📄</FileIcon>
                  <FileInfo>
                    <FileName>{summaryFile.name}</FileName>
                    <FileSize>{formatFileSize(summaryFile.size)}</FileSize>
                  </FileInfo>
                  <RemoveFileButton type="button" onClick={(e) => {
                    e.stopPropagation();
                    removeSummaryFile();
                  }}>
                    ×
                  </RemoveFileButton>
                </FilePreview>
              )}
            </FileUploadContainer>
          </FormGroup>

          <FormGroup>
            <Label>독서토론 적용 파일 (md)</Label>
            <FileUploadContainer onClick={handleApplicationFileClick}>
              <FileInput 
                type="file" 
                ref={applicationFileInputRef} 
                onChange={handleApplicationFileChange}
                accept=".md"
              />
              {!applicationFile ? (
                <>
                  <div>Drag and drop file here</div>
                  <div style={{ fontSize: '0.875rem', color: '#6b7280', marginTop: '0.5rem' }}>
                    Limit 200MB per file • MD
                  </div>
                </>
              ) : (
                <FilePreview>
                  <FileIcon>📄</FileIcon>
                  <FileInfo>
                    <FileName>{applicationFile.name}</FileName>
                    <FileSize>{formatFileSize(applicationFile.size)}</FileSize>
                  </FileInfo>
                  <RemoveFileButton type="button" onClick={(e) => {
                    e.stopPropagation();
                    removeApplicationFile();
                  }}>
                    ×
                  </RemoveFileButton>
                </FilePreview>
              )}
            </FileUploadContainer>
          </FormGroup>

          <FormGroup>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <Label htmlFor="summary">요약</Label>
              <Button 
                type="button" 
                onClick={generateAISummary}
                disabled={aiSummaryLoading}
              >
                {aiSummaryLoading && <LoadingSpinner />}
                {aiSummaryLoading ? 'AI 요약 생성 중...' : 'AI 요약 생성'}
              </Button>
            </div>
            <TextArea
              id="summary"
              {...register('summary', { required: '요약을 입력해주세요.' })}
              placeholder="토론 내용 요약을 입력하거나 AI 요약 생성 버튼을 클릭하세요."
            />
            {errors.summary && <ErrorMessage>{errors.summary.message}</ErrorMessage>}
          </FormGroup>

          <Button primary type="submit" disabled={loading}>
            {loading && <LoadingSpinner />}
            {loading ? '등록 중...' : '독서 토론 등록'}
          </Button>
        </form>
      </Card>
    </PageContainer>
  );
};

export default DiscussionRegister;
