import React, { useState, useEffect } from 'react';
import styled from '@emotion/styled';
import axios from 'axios';
import SimpleDatePicker from '../components/common/SimpleDatePicker';

const PageContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 2rem;
`;

const Card = styled.div`
  background: white;
  border-radius: 10px;
  padding: 2rem;
  margin-bottom: 2rem;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const Title = styled.h1`
  color: #1f2937;
  margin-bottom: 1.5rem;
  font-size: 2rem;
`;

const FormGroup = styled.div`
  margin-bottom: 1.5rem;
`;

const Label = styled.label`
  display: block;
  margin-bottom: 0.5rem;
  color: #4b5563;
  font-weight: 500;
`;

const Input = styled.input`
  width: 100%;
  padding: 0.75rem;
  border: 2px solid #e2e8f0;
  border-radius: 8px;
  font-size: 1rem;

  &:focus {
    border-color: #3b82f6;
    outline: none;
    box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
  }
`;

const Button = styled.button<{ primary?: boolean; loading?: boolean }>`
  padding: 0.75rem 1.5rem;
  border: none;
  border-radius: 8px;
  font-weight: 600;
  cursor: ${props => props.loading ? 'wait' : 'pointer'};
  opacity: ${props => props.loading ? 0.7 : 1};
  transition: all 0.3s ease;
  margin-right: 1rem;

  ${props => props.primary ? `
    background: #3b82f6;
    color: white;
    &:hover { background: #2563eb; }
  ` : `
    background: #e2e8f0;
    color: #1f2937;
    &:hover { background: #cbd5e1; }
  `}
`;

const Table = styled.table`
  width: 100%;
  border-collapse: collapse;
  margin-top: 1rem;
`;

const Th = styled.th`
  text-align: left;
  padding: 1rem;
  background-color: #f8fafc;
  border-bottom: 2px solid #e2e8f0;
  color: #4b5563;
`;

const Td = styled.td`
  padding: 1rem;
  border-bottom: 1px solid #e2e8f0;
`;

const Tr = styled.tr`
  &:hover {
    background-color: #f1f5f9;
  }
`;

const DetailsButton = styled.button`
  background: none;
  border: none;
  color: #3b82f6;
  cursor: pointer;
  text-decoration: underline;
  padding: 0;
  font-size: 0.875rem;
`;

const Modal = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 1000;
`;

const ModalContent = styled.div`
  background: white;
  padding: 2rem;
  border-radius: 10px;
  max-width: 800px;
  width: 90%;
  max-height: 90vh;
  overflow-y: auto;
`;

const ModalHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 1.5rem;
`;

const CloseButton = styled.button`
  background: none;
  border: none;
  font-size: 1.5rem;
  cursor: pointer;
  color: #4b5563;
`;

const ModalTitle = styled.h2`
  margin: 0;
  color: #1f2937;
`;

const DetailItem = styled.div`
  margin-bottom: 1.5rem;
`;

const DetailLabel = styled.h3`
  margin: 0 0 0.5rem 0;
  color: #4b5563;
  font-size: 1rem;
`;

const DetailValue = styled.div`
  color: #1f2937;
`;

const Tag = styled.span`
  background: #e2e8f0;
  padding: 0.25rem 0.5rem;
  border-radius: 4px;
  margin-right: 0.5rem;
  margin-bottom: 0.5rem;
  display: inline-block;
`;

const NoResults = styled.div`
  text-align: center;
  padding: 2rem;
  color: #6b7280;
`;

const SearchForm = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
  gap: 1rem;
`;

const DateRangeContainer = styled.div`
  display: flex;
  gap: 1rem;
  align-items: center;
`;

const LoadingSpinner = styled.div`
  display: inline-block;
  width: 20px;
  height: 20px;
  border: 3px solid #f3f3f3;
  border-top: 3px solid #3498db;
  border-radius: 50%;
  animation: spin 1s linear infinite;
  margin-right: 8px;

  @keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }
`;

const Select = styled.select`
  width: 100%;
  padding: 0.75rem;
  border: 2px solid #e2e8f0;
  border-radius: 8px;
  font-size: 1rem;

  &:focus {
    border-color: #3b82f6;
    outline: none;
    box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
  }
`;

// 상수 정의
const TYPE_OPTIONS = {
  "요약": "summary",
  "적용": "application",
  "적용 고급": "application_advanced",
  "적용 비교": "application_compare"
} as const;

const ANALYSIS_KEYWORDS = [
  "가치 창조",
  "마케팅",
  "세일즈",
  "가치 전달",
  "재무",
  "기타"
] as const;

// 상수 추가
const DEFAULT_BOOK_TITLES = [
  "퍼스널 MBA",
  "초격차",
  "마케팅 불변의 법칙"
] as const;

// 인터페이스 정의
interface Material {
  id: number;
  bookTitle: string;
  fileName: string;
  content: string;
  createdAt: string;
  type: keyof typeof TYPE_OPTIONS;
}

interface AIAnalysisResponse {
  analysis: string;
  audioUrl?: string;
}

const DiscussionSearch: React.FC = () => {
  const [bookTitle, setBookTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [startDate, setStartDate] = useState<Date | null>(null);
  const [endDate, setEndDate] = useState<Date | null>(null);
  const [showModal, setShowModal] = useState(false);
  const [materialType, setMaterialType] = useState('summary');
  const [analysisKeyword, setAnalysisKeyword] = useState('');
  const [customKeyword, setCustomKeyword] = useState('');
  const [previousTopic, setPreviousTopic] = useState('');
  const [nextTopic, setNextTopic] = useState('');
  const [materials, setMaterials] = useState<Material[]>([]);
  const [bookTitles, setBookTitles] = useState<string[]>([]);
  const [aiAnalysis, setAiAnalysis] = useState<string>('');
  const [audioUrl, setAudioUrl] = useState<string>('');
  const [selectedMaterial, setSelectedMaterial] = useState<Material | null>(null);
  const [loading, setLoading] = useState(false);
  const [analyzing, setAnalyzing] = useState(false);

  const resetSearch = () => {
    setBookTitle('');
    setMaterialType('summary');
    setAnalysisKeyword('');
    setCustomKeyword('');
    setPreviousTopic('');
    setNextTopic('');
    setMaterials([]);
  };

  const viewDetails = (material: Material) => {
    setSelectedMaterial(material);
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setSelectedMaterial(null);
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('ko-KR', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  // 책 목록 가져오기
  const fetchBookTitles = async () => {
    try {
      const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/books`);
      let titles = response.data;

      // 기본 책 목록과 서버에서 가져온 목록 합치기
      titles = Array.from(new Set([...DEFAULT_BOOK_TITLES, ...titles]));

      // "퍼스널 MBA"를 맨 앞으로 이동
      const index = titles.indexOf("퍼스널 MBA");
      if (index > 0) {
        titles = [
          "퍼스널 MBA",
          ...titles.slice(0, index),
          ...titles.slice(index + 1)
        ];
      }

      setBookTitles(titles);
      // 기본값으로 "퍼스널 MBA" 선택
      if (!bookTitle) {
        setBookTitle("퍼스널 MBA");
      }
    } catch (error) {
      console.error('책 목록 조회 오류:', error);
      // 오류 발생 시 기본 책 목록 사용
      setBookTitles(DEFAULT_BOOK_TITLES as unknown as string[]);
      setBookTitle("퍼스널 MBA");
    }
  };

  // 컴포넌트 마운트 시 책 목록 가져오기
  useEffect(() => {
    fetchBookTitles();
  }, []); // 의존성 배열 비움

  // AI 분석 요청 함수
  const requestAIAnalysis = async (content: string) => {
    try {
      setAnalyzing(true);
      const response = await axios.post(`${process.env.REACT_APP_API_URL}/api/analyze`, {
        content,
        type: materialType,
        keyword: analysisKeyword === '기타' ? customKeyword : analysisKeyword,
        previousTopic,
        nextTopic
      });

      setAiAnalysis(response.data.analysis);
      if (response.data.audioUrl) {
        setAudioUrl(response.data.audioUrl);
      }
    } catch (error) {
      console.error('AI 분석 오류:', error);
      alert('AI 분석 중 오류가 발생했습니다.');
    } finally {
      setAnalyzing(false);
    }
  };

  // 검색 함수 수정
  const searchMaterials = async () => {
    if (!bookTitle || !materialType) {
      alert('책과 자료 유형을 선택해주세요.');
      return;
    }

    setLoading(true);
    try {
      const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/materials`, {
        params: {
          bookTitle,
          type: TYPE_OPTIONS[materialType as keyof typeof TYPE_OPTIONS]
        }
      });

      if (response.data.length === 0) {
        alert(`${bookTitle}의 ${materialType} 자료가 없습니다.`);
      }
      setMaterials(response.data);
    } catch (error) {
      console.error('자료 검색 오류:', error);
      alert('자료 검색 중 오류가 발생했습니다.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <PageContainer>
      <Title>🔍 독서 토론 검색</Title>

      <Card>
        <SearchForm>
          <FormGroup>
            <Label htmlFor="bookTitle">책 선택</Label>
            <Select
              id="bookTitle"
              value={bookTitle}
              onChange={(e) => setBookTitle(e.target.value)}
            >
              {bookTitles.map(title => (
                <option key={title} value={title}>{title}</option>
              ))}
            </Select>
          </FormGroup>

          <FormGroup>
            <Label htmlFor="materialType">자료 유형</Label>
            <Select
              id="materialType"
              value={materialType}
              onChange={(e) => {
                setMaterialType(e.target.value);
                // 자료 유형 변경 시 관련 상태 초기화
                setAiAnalysis('');
                setAudioUrl('');
                setPreviousTopic('');
                setNextTopic('');
                setAnalysisKeyword(ANALYSIS_KEYWORDS[0]);
                setCustomKeyword('');
              }}
            >
              {Object.keys(TYPE_OPTIONS).map(label => (
                <option key={label} value={label}>{label}</option>
              ))}
            </Select>
          </FormGroup>

          {materialType === 'summary' && (
            <FormGroup>
              <Label htmlFor="previousTopic">이전 토론 주제</Label>
              <Input
                id="previousTopic"
                value={previousTopic}
                onChange={(e) => setPreviousTopic(e.target.value)}
                placeholder="이전 독서 토론의 주제를 입력해주세요"
              />
            </FormGroup>
          )}

          {materialType === 'application' && (
            <FormGroup>
              <Label htmlFor="nextTopic">다음 토론 주제</Label>
              <Input
                id="nextTopic"
                value={nextTopic}
                onChange={(e) => setNextTopic(e.target.value)}
                placeholder="다음 독서 토론의 주제를 입력해주세요"
              />
            </FormGroup>
          )}

          {['application', 'application_advanced', 'application_compare'].includes(materialType) && (
            <FormGroup>
              <Label htmlFor="analysisKeyword">분석 키워드</Label>
              <Select
                id="analysisKeyword"
                value={analysisKeyword}
                onChange={(e) => setAnalysisKeyword(e.target.value)}
              >
                {ANALYSIS_KEYWORDS.map(keyword => (
                  <option key={keyword} value={keyword}>{keyword}</option>
                ))}
              </Select>
              {analysisKeyword === '기타' && (
                <Input
                  value={customKeyword}
                  onChange={(e) => setCustomKeyword(e.target.value)}
                  placeholder="키워드를 입력하세요"
                  style={{ marginTop: '0.5rem' }}
                />
              )}
            </FormGroup>
          )}
        </SearchForm>

        <div style={{ marginTop: '1rem' }}>
          <Button primary onClick={searchMaterials} disabled={loading}>
            {loading && <LoadingSpinner />}
            {loading ? '검색 중...' : '검색'}
          </Button>
          <Button onClick={resetSearch}>초기화</Button>
        </div>
      </Card>

      <Card>
        <h2>검색 결과</h2>

        {materials.length > 0 ? (
          <Table>
            <thead>
              <tr>
                <Th>책 제목</Th>
                <Th>파일 이름</Th>
                <Th>작성 날짜</Th>
                <Th>상세 정보</Th>
              </tr>
            </thead>
            <tbody>
              {materials.map((material) => (
                <Tr key={material.id}>
                  <Td>{material.bookTitle}</Td>
                  <Td>{material.fileName}</Td>
                  <Td>{formatDate(material.createdAt)}</Td>
                  <Td>
                    <DetailsButton onClick={() => viewDetails(material)}>
                      상세 보기
                    </DetailsButton>
                  </Td>
                </Tr>
              ))}
            </tbody>
          </Table>
        ) : (
          <NoResults>
            검색 결과가 없습니다.
          </NoResults>
        )}
      </Card>

      {showModal && selectedMaterial && (
        <Modal onClick={(e) => e.target === e.currentTarget && closeModal()}>
          <ModalContent onClick={(e) => e.stopPropagation()}>
            <ModalHeader>
              <ModalTitle>{selectedMaterial.fileName}</ModalTitle>
              <CloseButton onClick={closeModal}>×</CloseButton>
            </ModalHeader>

            <DetailItem>
              <DetailLabel>책 제목</DetailLabel>
              <DetailValue>{selectedMaterial.bookTitle}</DetailValue>
            </DetailItem>

            <DetailItem>
              <DetailLabel>작성 날짜</DetailLabel>
              <DetailValue>{formatDate(selectedMaterial.createdAt)}</DetailValue>
            </DetailItem>

            <DetailItem>
              <DetailLabel>내용</DetailLabel>
              <DetailValue style={{ whiteSpace: 'pre-line' }}>
                {selectedMaterial.content}
              </DetailValue>
            </DetailItem>

            {['application', 'application_advanced'].includes(selectedMaterial.type) && (
              <DetailItem>
                <DetailLabel>AI 분석</DetailLabel>
                {!aiAnalysis ? (
                  <Button 
                    onClick={() => requestAIAnalysis(selectedMaterial.content)}
                    disabled={analyzing}
                  >
                    {analyzing ? 'AI 분석 중...' : 'AI 분석 시작'}
                  </Button>
                ) : (
                  <>
                    <DetailValue style={{ whiteSpace: 'pre-line' }}>
                      {aiAnalysis}
                    </DetailValue>
                    {audioUrl && (
                      <audio controls src={audioUrl} style={{ marginTop: '1rem' }}>
                        Your browser does not support the audio element.
                      </audio>
                    )}
                  </>
                )}
              </DetailItem>
            )}
          </ModalContent>
        </Modal>
      )}
    </PageContainer>
  );
};

export default DiscussionSearch;
