import React, { useState, useEffect } from 'react';
import styled from '@emotion/styled';
import axios from 'axios';
import SimpleDatePicker from '../components/common/SimpleDatePicker';

const PageContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 2rem;
`;

const Card = styled.div`
  background: white;
  border-radius: 10px;
  padding: 2rem;
  margin-bottom: 2rem;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const Title = styled.h1`
  color: #1f2937;
  margin-bottom: 1.5rem;
  font-size: 2rem;
`;

const FormGroup = styled.div`
  margin-bottom: 1.5rem;
`;

const Label = styled.label`
  display: block;
  margin-bottom: 0.5rem;
  color: #4b5563;
  font-weight: 500;
`;

const Input = styled.input`
  width: 100%;
  padding: 0.75rem;
  border: 2px solid #e2e8f0;
  border-radius: 8px;
  font-size: 1rem;

  &:focus {
    border-color: #3b82f6;
    outline: none;
    box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
  }
`;

const Button = styled.button<{ primary?: boolean; loading?: boolean }>`
  padding: 0.75rem 1.5rem;
  border: none;
  border-radius: 8px;
  font-weight: 600;
  cursor: ${props => props.loading ? 'wait' : 'pointer'};
  opacity: ${props => props.loading ? 0.7 : 1};
  transition: all 0.3s ease;
  margin-right: 1rem;

  ${props => props.primary ? `
    background: #3b82f6;
    color: white;
    &:hover { background: #2563eb; }
  ` : `
    background: #e2e8f0;
    color: #1f2937;
    &:hover { background: #cbd5e1; }
  `}
`;

const Table = styled.table`
  width: 100%;
  border-collapse: collapse;
  margin-top: 1rem;
`;

const Th = styled.th`
  text-align: left;
  padding: 1rem;
  background-color: #f8fafc;
  border-bottom: 2px solid #e2e8f0;
  color: #4b5563;
`;

const Td = styled.td`
  padding: 1rem;
  border-bottom: 1px solid #e2e8f0;
`;

const Tr = styled.tr`
  &:hover {
    background-color: #f1f5f9;
  }
`;

const DetailsButton = styled.button`
  background: none;
  border: none;
  color: #3b82f6;
  cursor: pointer;
  text-decoration: underline;
  padding: 0;
  font-size: 0.875rem;
`;

const Modal = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 1000;
`;

const ModalContent = styled.div`
  background: white;
  padding: 2rem;
  border-radius: 10px;
  max-width: 800px;
  width: 90%;
  max-height: 90vh;
  overflow-y: auto;
`;

const ModalHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 1.5rem;
`;

const CloseButton = styled.button`
  background: none;
  border: none;
  font-size: 1.5rem;
  cursor: pointer;
  color: #4b5563;
`;

const ModalTitle = styled.h2`
  margin: 0;
  color: #1f2937;
`;

const DetailItem = styled.div`
  margin-bottom: 1.5rem;
`;

const DetailLabel = styled.h3`
  margin: 0 0 0.5rem 0;
  color: #4b5563;
  font-size: 1rem;
`;

const DetailValue = styled.div`
  color: #1f2937;
`;

const Tag = styled.span`
  background: #e2e8f0;
  padding: 0.25rem 0.5rem;
  border-radius: 4px;
  margin-right: 0.5rem;
  margin-bottom: 0.5rem;
  display: inline-block;
`;

const NoResults = styled.div`
  text-align: center;
  padding: 2rem;
  color: #6b7280;
`;

const SearchForm = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
  gap: 1rem;
`;

const DateRangeContainer = styled.div`
  display: flex;
  gap: 1rem;
  align-items: center;
`;

const LoadingSpinner = styled.div`
  display: inline-block;
  width: 20px;
  height: 20px;
  border: 3px solid #f3f3f3;
  border-top: 3px solid #3498db;
  border-radius: 50%;
  animation: spin 1s linear infinite;
  margin-right: 8px;

  @keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }
`;

const Select = styled.select`
  width: 100%;
  padding: 0.75rem;
  border: 2px solid #e2e8f0;
  border-radius: 8px;
  font-size: 1rem;

  &:focus {
    border-color: #3b82f6;
    outline: none;
    box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
  }
`;

// ÏÉÅÏàò Ï†ïÏùò
const TYPE_OPTIONS = {
  "ÏöîÏïΩ": "summary",
  "Ï†ÅÏö©": "application",
  "Ï†ÅÏö© Í≥†Í∏â": "application_advanced",
  "Ï†ÅÏö© ÎπÑÍµê": "application_compare"
} as const;

const ANALYSIS_KEYWORDS = [
  "Í∞ÄÏπò Ï∞ΩÏ°∞",
  "ÎßàÏºÄÌåÖ",
  "ÏÑ∏ÏùºÏ¶à",
  "Í∞ÄÏπò Ï†ÑÎã¨",
  "Ïû¨Î¨¥",
  "Í∏∞ÌÉÄ"
] as const;

// Ïù∏ÌÑ∞ÌéòÏù¥Ïä§ Ï†ïÏùò
interface Material {
  id: number;
  bookTitle: string;
  fileName: string;
  content: string;
  createdAt: string;
  type: keyof typeof TYPE_OPTIONS;
}

interface AIAnalysisResponse {
  analysis: string;
  audioUrl?: string;
}

const DiscussionSearch: React.FC = () => {
  const [bookTitle, setBookTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [startDate, setStartDate] = useState<Date | null>(null);
  const [endDate, setEndDate] = useState<Date | null>(null);
  const [showModal, setShowModal] = useState(false);
  const [materialType, setMaterialType] = useState('summary');
  const [analysisKeyword, setAnalysisKeyword] = useState('');
  const [customKeyword, setCustomKeyword] = useState('');
  const [previousTopic, setPreviousTopic] = useState('');
  const [nextTopic, setNextTopic] = useState('');
  const [materials, setMaterials] = useState<Material[]>([]);
  const [bookTitles, setBookTitles] = useState<string[]>([]);
  const [aiAnalysis, setAiAnalysis] = useState<string>('');
  const [audioUrl, setAudioUrl] = useState<string>('');
  const [selectedMaterial, setSelectedMaterial] = useState<Material | null>(null);
  const [loading, setLoading] = useState(false);
  const [analyzing, setAnalyzing] = useState(false);

  const resetSearch = () => {
    setBookTitle('');
    setMaterialType('summary');
    setAnalysisKeyword('');
    setCustomKeyword('');
    setPreviousTopic('');
    setNextTopic('');
    setMaterials([]);
  };

  const viewDetails = (material: Material) => {
    setSelectedMaterial(material);
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setSelectedMaterial(null);
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('ko-KR', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  useEffect(() => {
    searchMaterials();
  }, []);

  // Ï±Ö Î™©Î°ù Í∞ÄÏ†∏Ïò§Í∏∞
  useEffect(() => {
    const fetchBookTitles = async () => {
      try {
        const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/books`);
        let titles = response.data;

        // "ÌçºÏä§ÎÑê MBA"Í∞Ä ÏóÜÏúºÎ©¥ Î™©Î°ù Îß® ÏïûÏóê Ï∂îÍ∞Ä
        if (!titles.includes("ÌçºÏä§ÎÑê MBA")) {
          titles = ["ÌçºÏä§ÎÑê MBA", ...titles];
        } else {
          // "ÌçºÏä§ÎÑê MBA"Í∞Ä ÏûàÏßÄÎßå Ï≤´ Î≤àÏß∏Í∞Ä ÏïÑÎãå Í≤ΩÏö∞, Îß® ÏïûÏúºÎ°ú Ïù¥Îèô
          const index = titles.indexOf("ÌçºÏä§ÎÑê MBA");
          if (index > 0) {
            titles = [
              "ÌçºÏä§ÎÑê MBA",
              ...titles.slice(0, index),
              ...titles.slice(index + 1)
            ];
          }
        }

        setBookTitles(titles);
        // Í∏∞Î≥∏Í∞íÏúºÎ°ú "ÌçºÏä§ÎÑê MBA" ÏÑ†ÌÉù
        if (!bookTitle) {
          setBookTitle("ÌçºÏä§ÎÑê MBA");
        }
      } catch (error) {
        console.error('Ï±Ö Î™©Î°ù Ï°∞Ìöå Ïò§Î•ò:', error);
        // Ïò§Î•ò Î∞úÏÉù Ïãú Í∏∞Î≥∏Í∞íÏúºÎ°ú "ÌçºÏä§ÎÑê MBA" ÏÑ§Ï†ï
        setBookTitles(["ÌçºÏä§ÎÑê MBA"]);
        setBookTitle("ÌçºÏä§ÎÑê MBA");
      }
    };
    fetchBookTitles();
  }, [bookTitle]); // bookTitleÏùÑ ÏùòÏ°¥ÏÑ± Î∞∞Ïó¥Ïóê Ï∂îÍ∞Ä

  // AI Î∂ÑÏÑù ÏöîÏ≤≠ Ìï®Ïàò
  const requestAIAnalysis = async (content: string) => {
    try {
      setAnalyzing(true);
      const response = await axios.post(`${process.env.REACT_APP_API_URL}/api/analyze`, {
        content,
        type: materialType,
        keyword: analysisKeyword === 'Í∏∞ÌÉÄ' ? customKeyword : analysisKeyword,
        previousTopic,
        nextTopic
      });

      setAiAnalysis(response.data.analysis);
      if (response.data.audioUrl) {
        setAudioUrl(response.data.audioUrl);
      }
    } catch (error) {
      console.error('AI Î∂ÑÏÑù Ïò§Î•ò:', error);
      alert('AI Î∂ÑÏÑù Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
    } finally {
      setAnalyzing(false);
    }
  };

  // Í≤ÄÏÉâ Ìï®Ïàò ÏàòÏ†ï
  const searchMaterials = async () => {
    setLoading(true);
    try {
      const params: any = {
        bookTitle,
        type: materialType,
        keyword: analysisKeyword === 'Í∏∞ÌÉÄ' ? customKeyword : analysisKeyword
      };

      const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/materials`, { params });
      setMaterials(response.data);
    } catch (error) {
      console.error('ÏûêÎ£å Í≤ÄÏÉâ Ïò§Î•ò:', error);
      alert('ÏûêÎ£å Í≤ÄÏÉâ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <PageContainer>
      <Title>üîç ÎèÖÏÑú ÌÜ†Î°† Í≤ÄÏÉâ</Title>

      <Card>
        <SearchForm>
          <FormGroup>
            <Label htmlFor="bookTitle">Ï±Ö ÏÑ†ÌÉù</Label>
            <Select
              id="bookTitle"
              value={bookTitle}
              onChange={(e) => setBookTitle(e.target.value)}
            >
              {bookTitles.map(title => (
                <option key={title} value={title}>{title}</option>
              ))}
            </Select>
          </FormGroup>

          <FormGroup>
            <Label htmlFor="materialType">ÏûêÎ£å Ïú†Ìòï</Label>
            <Select
              id="materialType"
              value={materialType}
              onChange={(e) => setMaterialType(e.target.value)}
            >
              {Object.entries(TYPE_OPTIONS).map(([label, value]) => (
                <option key={value} value={value}>{label}</option>
              ))}
            </Select>
          </FormGroup>

          {materialType === 'summary' && (
            <FormGroup>
              <Label htmlFor="previousTopic">Ïù¥Ï†Ñ ÌÜ†Î°† Ï£ºÏ†ú</Label>
              <Input
                id="previousTopic"
                value={previousTopic}
                onChange={(e) => setPreviousTopic(e.target.value)}
                placeholder="Ïù¥Ï†Ñ ÎèÖÏÑú ÌÜ†Î°†Ïùò Ï£ºÏ†úÎ•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî"
              />
            </FormGroup>
          )}

          {materialType === 'application' && (
            <FormGroup>
              <Label htmlFor="nextTopic">Îã§Ïùå ÌÜ†Î°† Ï£ºÏ†ú</Label>
              <Input
                id="nextTopic"
                value={nextTopic}
                onChange={(e) => setNextTopic(e.target.value)}
                placeholder="Îã§Ïùå ÎèÖÏÑú ÌÜ†Î°†Ïùò Ï£ºÏ†úÎ•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî"
              />
            </FormGroup>
          )}

          {['application', 'application_advanced', 'application_compare'].includes(materialType) && (
            <FormGroup>
              <Label htmlFor="analysisKeyword">Î∂ÑÏÑù ÌÇ§ÏõåÎìú</Label>
              <Select
                id="analysisKeyword"
                value={analysisKeyword}
                onChange={(e) => setAnalysisKeyword(e.target.value)}
              >
                {ANALYSIS_KEYWORDS.map(keyword => (
                  <option key={keyword} value={keyword}>{keyword}</option>
                ))}
              </Select>
              {analysisKeyword === 'Í∏∞ÌÉÄ' && (
                <Input
                  value={customKeyword}
                  onChange={(e) => setCustomKeyword(e.target.value)}
                  placeholder="ÌÇ§ÏõåÎìúÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
                  style={{ marginTop: '0.5rem' }}
                />
              )}
            </FormGroup>
          )}
        </SearchForm>

        <div style={{ marginTop: '1rem' }}>
          <Button primary onClick={searchMaterials} disabled={loading}>
            {loading && <LoadingSpinner />}
            {loading ? 'Í≤ÄÏÉâ Ï§ë...' : 'Í≤ÄÏÉâ'}
          </Button>
          <Button onClick={resetSearch}>Ï¥àÍ∏∞Ìôî</Button>
        </div>
      </Card>

      <Card>
        <h2>Í≤ÄÏÉâ Í≤∞Í≥º</h2>

        {materials.length > 0 ? (
          <Table>
            <thead>
              <tr>
                <Th>Ï±Ö Ï†úÎ™©</Th>
                <Th>ÌååÏùº Ïù¥Î¶Ñ</Th>
                <Th>ÏûëÏÑ± ÎÇ†Ïßú</Th>
                <Th>ÏÉÅÏÑ∏ Ï†ïÎ≥¥</Th>
              </tr>
            </thead>
            <tbody>
              {materials.map((material) => (
                <Tr key={material.id}>
                  <Td>{material.bookTitle}</Td>
                  <Td>{material.fileName}</Td>
                  <Td>{formatDate(material.createdAt)}</Td>
                  <Td>
                    <DetailsButton onClick={() => viewDetails(material)}>
                      ÏÉÅÏÑ∏ Î≥¥Í∏∞
                    </DetailsButton>
                  </Td>
                </Tr>
              ))}
            </tbody>
          </Table>
        ) : (
          <NoResults>
            Í≤ÄÏÉâ Í≤∞Í≥ºÍ∞Ä ÏóÜÏäµÎãàÎã§.
          </NoResults>
        )}
      </Card>

      {showModal && selectedMaterial && (
        <Modal onClick={(e) => e.target === e.currentTarget && closeModal()}>
          <ModalContent onClick={(e) => e.stopPropagation()}>
            <ModalHeader>
              <ModalTitle>{selectedMaterial.fileName}</ModalTitle>
              <CloseButton onClick={closeModal}>√ó</CloseButton>
            </ModalHeader>

            <DetailItem>
              <DetailLabel>Ï±Ö Ï†úÎ™©</DetailLabel>
              <DetailValue>{selectedMaterial.bookTitle}</DetailValue>
            </DetailItem>

            <DetailItem>
              <DetailLabel>ÏûëÏÑ± ÎÇ†Ïßú</DetailLabel>
              <DetailValue>{formatDate(selectedMaterial.createdAt)}</DetailValue>
            </DetailItem>

            <DetailItem>
              <DetailLabel>ÎÇ¥Ïö©</DetailLabel>
              <DetailValue style={{ whiteSpace: 'pre-line' }}>
                {selectedMaterial.content}
              </DetailValue>
            </DetailItem>

            {['application', 'application_advanced'].includes(selectedMaterial.type) && (
              <DetailItem>
                <DetailLabel>AI Î∂ÑÏÑù</DetailLabel>
                {!aiAnalysis ? (
                  <Button 
                    onClick={() => requestAIAnalysis(selectedMaterial.content)}
                    disabled={analyzing}
                  >
                    {analyzing ? 'AI Î∂ÑÏÑù Ï§ë...' : 'AI Î∂ÑÏÑù ÏãúÏûë'}
                  </Button>
                ) : (
                  <>
                    <DetailValue style={{ whiteSpace: 'pre-line' }}>
                      {aiAnalysis}
                    </DetailValue>
                    {audioUrl && (
                      <audio controls src={audioUrl} style={{ marginTop: '1rem' }}>
                        Your browser does not support the audio element.
                      </audio>
                    )}
                  </>
                )}
              </DetailItem>
            )}
          </ModalContent>
        </Modal>
      )}
    </PageContainer>
  );
};

export default DiscussionSearch;
