import React, { useState, useRef } from 'react';
import styled from '@emotion/styled';
import { useForm } from 'react-hook-form';
import axios from 'axios';
import SimpleDatePicker from '../components/common/SimpleDatePicker';

const PageContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 2rem;
`;

const Card = styled.div`
  background: white;
  border-radius: 10px;
  padding: 2rem;
  margin-bottom: 2rem;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const Title = styled.h1`
  color: #1f2937;
  margin-bottom: 1.5rem;
  font-size: 2rem;
`;

const FormGroup = styled.div`
  margin-bottom: 1.5rem;
`;

const Label = styled.label`
  display: block;
  margin-bottom: 0.5rem;
  color: #4b5563;
  font-weight: 500;
`;

const Select = styled.select`
  width: 100%;
  padding: 0.75rem;
  border: 2px solid #e2e8f0;
  border-radius: 8px;
  font-size: 1rem;

  &:focus {
    border-color: #3b82f6;
    outline: none;
    box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
  }
`;

const Button = styled.button<{ primary?: boolean; loading?: boolean }>`
  padding: 0.75rem 1.5rem;
  border: none;
  border-radius: 8px;
  font-weight: 600;
  cursor: ${props => props.loading ? 'wait' : 'pointer'};
  opacity: ${props => props.loading ? 0.7 : 1};
  transition: all 0.3s ease;
  margin-right: 1rem;

  ${props => props.primary ? `
    background: #3b82f6;
    color: white;
    &:hover { background: #2563eb; }
  ` : `
    background: #e2e8f0;
    color: #1f2937;
    &:hover { background: #cbd5e1; }
  `}
`;

const ErrorMessage = styled.p`
  color: #ef4444;
  font-size: 0.875rem;
  margin-top: 0.5rem;
`;

const SuccessMessage = styled.div`
  background-color: #10b981;
  color: white;
  padding: 1rem;
  border-radius: 8px;
  margin-bottom: 1rem;
`;

const LoadingSpinner = styled.div`
  display: inline-block;
  width: 20px;
  height: 20px;
  border: 3px solid #f3f3f3;
  border-top: 3px solid #3498db;
  border-radius: 50%;
  animation: spin 1s linear infinite;
  margin-right: 8px;

  @keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }
`;

// 파일 업로드 관련 스타일
const FileUploadContainer = styled.div`
  border: 2px dashed #e2e8f0;
  border-radius: 8px;
  padding: 2rem;
  text-align: center;
  margin-bottom: 1.5rem;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    border-color: #3b82f6;
    background-color: #f8fafc;
  }
`;

const FileInput = styled.input`
  display: none;
`;

const FilePreview = styled.div`
  margin-top: 1rem;
  padding: 1rem;
  background-color: #f8fafc;
  border-radius: 8px;
  display: flex;
  align-items: center;
  justify-content: space-between;
`;

const FileIcon = styled.div`
  margin-right: 1rem;
  color: #3b82f6;
  font-size: 1.5rem;
`;

const FileInfo = styled.div`
  flex: 1;
  text-align: left;
`;

const FileName = styled.div`
  font-weight: 500;
  margin-bottom: 0.25rem;
`;

const FileSize = styled.div`
  font-size: 0.875rem;
  color: #6b7280;
`;

const RemoveFileButton = styled.button`
  background: none;
  border: none;
  color: #ef4444;
  cursor: pointer;
  font-size: 1.25rem;
`;

interface FormData {
  bookTitle: string;
}

// 책 목록 데이터
const bookOptions = [
  { value: "퍼스널 MBA", label: "퍼스널 MBA" },
  { value: "아주 작은 습관의 힘", label: "아주 작은 습관의 힘" },
  { value: "사피엔스", label: "사피엔스" },
  { value: "생각에 관한 생각", label: "생각에 관한 생각" },
  { value: "데일 카네기의 인간관계론", label: "데일 카네기의 인간관계론" }
];

const DiscussionRegister: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);

  // 파일 업로드 관련 상태
  const [summaryFile, setSummaryFile] = useState<File | null>(null);
  const [applicationFile, setApplicationFile] = useState<File | null>(null);
  const summaryFileInputRef = useRef<HTMLInputElement>(null);
  const applicationFileInputRef = useRef<HTMLInputElement>(null);

  const { register, handleSubmit, formState: { errors }, reset } = useForm<FormData>({
    defaultValues: {
      bookTitle: "퍼스널 MBA" // 기본값 설정
    }
  });

  // 파일 크기 포맷팅 함수
  const formatFileSize = (bytes: number): string => {
    if (bytes < 1024) return bytes + ' bytes';
    else if (bytes < 1048576) return (bytes / 1024).toFixed(1) + ' KB';
    else return (bytes / 1048576).toFixed(1) + ' MB';
  };

  // 파일 업로드 관련 함수
  const handleSummaryFileClick = () => {
    if (summaryFileInputRef.current) {
      summaryFileInputRef.current.click();
    }
  };

  const handleApplicationFileClick = () => {
    if (applicationFileInputRef.current) {
      applicationFileInputRef.current.click();
    }
  };

  const handleSummaryFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files.length > 0) {
      const file = e.target.files[0];
      if (file.type === 'text/markdown' || file.name.endsWith('.md')) {
        setSummaryFile(file);
      } else {
        alert('마크다운(.md) 파일만 업로드 가능합니다.');
      }
    }
  };

  const handleApplicationFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files.length > 0) {
      const file = e.target.files[0];
      if (file.type === 'text/markdown' || file.name.endsWith('.md')) {
        setApplicationFile(file);
      } else {
        alert('마크다운(.md) 파일만 업로드 가능합니다.');
      }
    }
  };

  const removeSummaryFile = () => {
    setSummaryFile(null);
    if (summaryFileInputRef.current) {
      summaryFileInputRef.current.value = '';
    }
  };

  const removeApplicationFile = () => {
    setApplicationFile(null);
    if (applicationFileInputRef.current) {
      applicationFileInputRef.current.value = '';
    }
  };

  const onSubmit = async (data: FormData) => {
    if (!summaryFile) {
      alert('독서토론 요약 파일(md)을 업로드해주세요.');
      return;
    }

    if (!applicationFile) {
      alert('독서토론 적용 파일(md)을 업로드해주세요.');
      return;
    }

    setLoading(true);
    try {
      // FormData 객체 생성
      const formData = new FormData();
      formData.append('bookTitle', data.bookTitle);
      formData.append('summaryFile', summaryFile);
      formData.append('applicationFile', applicationFile);

      console.log('요청 데이터:', {
        bookTitle: data.bookTitle,
        summaryFileName: summaryFile?.name,
        applicationFileName: applicationFile?.name
      });

      const apiUrl = `${process.env.REACT_APP_API_URL}/discussions`;
      console.log('API URL:', apiUrl);

      const response = await axios.post(apiUrl, formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      console.log('응답 데이터:', response.data);

      setSuccess(true);

      // 폼 초기화
      reset();
      setSummaryFile(null);
      setApplicationFile(null);

      // 3초 후 성공 메시지 숨기기
      setTimeout(() => {
        setSuccess(false);
      }, 3000);
    } catch (error) {
      console.error('독서 토론 등록 오류:', error);

      // 상세 오류 정보 표시
      if (axios.isAxiosError(error) && error.response) {
        alert(`오류: ${error.response.data.error || '서버 오류가 발생했습니다.'}`);
      } else {
        alert('독서 토론 등록 중 오류가 발생했습니다.');
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <PageContainer>
      <Title>📚 독서 토론 등록</Title>

      {success && (
        <SuccessMessage>
          독서 토론이 성공적으로 등록되었습니다!
        </SuccessMessage>
      )}

      <Card>
        <form onSubmit={handleSubmit(onSubmit)}>
          <FormGroup>
            <Label htmlFor="bookTitle">책 제목</Label>
            <Select
              id="bookTitle"
              {...register('bookTitle', { required: '책 제목을 선택해주세요.' })}
            >
              {bookOptions.map(option => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </Select>
            {errors.bookTitle && <ErrorMessage>{errors.bookTitle.message}</ErrorMessage>}
          </FormGroup>

          <FormGroup>
            <Label>독서토론 요약 파일 (md)</Label>
            <FileUploadContainer onClick={handleSummaryFileClick}>
              <FileInput 
                type="file" 
                ref={summaryFileInputRef} 
                onChange={handleSummaryFileChange}
                accept=".md"
              />
              {!summaryFile ? (
                <>
                  <div>Drag and drop file here</div>
                  <div style={{ fontSize: '0.875rem', color: '#6b7280', marginTop: '0.5rem' }}>
                    Limit 200MB per file • MD
                  </div>
                </>
              ) : (
                <FilePreview>
                  <FileIcon>📄</FileIcon>
                  <FileInfo>
                    <FileName>{summaryFile.name}</FileName>
                    <FileSize>{formatFileSize(summaryFile.size)}</FileSize>
                  </FileInfo>
                  <RemoveFileButton type="button" onClick={(e) => {
                    e.stopPropagation();
                    removeSummaryFile();
                  }}>
                    ×
                  </RemoveFileButton>
                </FilePreview>
              )}
            </FileUploadContainer>
          </FormGroup>

          <FormGroup>
            <Label>독서토론 적용 파일 (md)</Label>
            <FileUploadContainer onClick={handleApplicationFileClick}>
              <FileInput 
                type="file" 
                ref={applicationFileInputRef} 
                onChange={handleApplicationFileChange}
                accept=".md"
              />
              {!applicationFile ? (
                <>
                  <div>Drag and drop file here</div>
                  <div style={{ fontSize: '0.875rem', color: '#6b7280', marginTop: '0.5rem' }}>
                    Limit 200MB per file • MD
                  </div>
                </>
              ) : (
                <FilePreview>
                  <FileIcon>📄</FileIcon>
                  <FileInfo>
                    <FileName>{applicationFile.name}</FileName>
                    <FileSize>{formatFileSize(applicationFile.size)}</FileSize>
                  </FileInfo>
                  <RemoveFileButton type="button" onClick={(e) => {
                    e.stopPropagation();
                    removeApplicationFile();
                  }}>
                    ×
                  </RemoveFileButton>
                </FilePreview>
              )}
            </FileUploadContainer>
          </FormGroup>

          <Button primary type="submit" disabled={loading}>
            {loading && <LoadingSpinner />}
            {loading ? '등록 중...' : '독서 토론 등록'}
          </Button>
        </form>
      </Card>
    </PageContainer>
  );
};

export default DiscussionRegister;
